import React, { Fragment, useContext, useState, useEffect } from "react";
import { ProductContext } from "./index";
import { createProduct, getAllProduct } from "./FetchApi";
import { getAllCategory } from "../categories/FetchApi";

const AddProductDetail = ({ categories }) => {
  const { data, dispatch } = useContext(ProductContext);

  const alert = (msg, type) => (
    <div className={`bg-${type}-200 py-2 px-4 w-full`}>{msg}</div>
  );

  const [fData, setFdata] = useState({
    timeAmount: "",
    timeUnit: "",
    pDescription: "",
    pStatus: "Active",
    pImage: null,
    pCategory: "",
    pPrice: "",
    pOffer: 0,
    pQuantity: "",
    success: false,
    error: false,
  });

  const [submitting, setSubmitting] = useState(false);

  const fetchData = async () => {
    let responseData = await getAllProduct();
    setTimeout(() => {
      if (responseData && responseData.Products) {
        dispatch({
          type: "fetchProductsAndChangeState",
          payload: responseData.Products,
        });
      }
    }, 1000);
  };

  const submitForm = async (e) => {
    e.preventDefault();

    // Prevent double submission
    if (submitting) return;
    setSubmitting(true);

    // Validations
    if (!fData.timeAmount || !fData.timeUnit) {
      setFdata({ ...fData, error: "Please specify a valid time window" });
      setSubmitting(false);
      return setTimeout(() => {
        setFdata({ ...fData, error: false });
      }, 2000);
    }

    if (!fData.pImage || fData.pImage.length < 2) {
      setFdata({ ...fData, error: "Please upload at least 2 images" });
      setSubmitting(false);
      return setTimeout(() => {
        setFdata({ ...fData, error: false });
      }, 2000);
    }

    const formattedTimeWindow = `${fData.timeAmount} ${fData.timeUnit}`;

    try {
      let responseData = await createProduct({
        ...fData,
        pName: formattedTimeWindow, // store the time window as name
      });

      if (responseData.success) {
        fetchData();
        setFdata({
          timeAmount: "",
          timeUnit: "",
          pDescription: "",
          pImage: null,
          pStatus: "Active",
          pCategory: "",
          pPrice: "",
          pOffer: 0,
          pQuantity: "",
          success: responseData.success,
          error: false,
        });
        setTimeout(() => {
          setFdata((prev) => ({
            ...prev,
            success: false,
            error: false,
          }));
        }, 2000);
      } else if (responseData.error) {
        setFdata({ ...fData, success: false, error: responseData.error });
        setTimeout(() => {
          setFdata({ ...fData, error: false, success: false });
        }, 2000);
      }
    } catch (error) {
      console.log(error);
    }

    setSubmitting(false);
  };

  const handleNumericInput = (e, key) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFdata({
        ...fData,
        error: false,
        success: false,
        [key]: value,
      });
    }
  };

  return (
    <Fragment>
      <div
        onClick={() => dispatch({ type: "addProductModal", payload: false })}
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed top-0 left-0 z-30 w-full h-full bg-black opacity-50`}
      />
      <div
        className={`${
          data.addProductModal ? "" : "hidden"
        } fixed inset-0 flex items-center z-30 justify-center overflow-auto`}
      >
        <div className="mt-32 md:mt-0 relative bg-white w-11/12 md:w-3/6 shadow-lg flex flex-col items-center space-y-4 px-4 py-4 md:px-8">
          <div className="flex items-center justify-between w-full pt-4">
            <span className="text-left font-semibold text-2xl tracking-wider">
              Add Organ
            </span>
            <span
              style={{ background: "#303031" }}
              onClick={() =>
                dispatch({ type: "addProductModal", payload: false })
              }
              className="cursor-pointer text-gray-100 py-2 px-2 rounded-full"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </span>
          </div>
          {fData.error ? alert(fData.error, "red") : ""}
          {fData.success ? alert(fData.success, "green") : ""}
          <form className="w-full" onSubmit={submitForm}>
            <div className="flex space-x-1 py-4">
              <div className="w-1/2 flex flex-col space-y-1">
                <label htmlFor="timeAmount">Time Window *</label>
                <div className="flex space-x-2">
                  <input
                    type="number"
                    min="1"
                    value={fData.timeAmount}
                    onChange={(e) =>
                      handleNumericInput(e, "timeAmount")
                    }
                    className="w-1/2 px-4 py-2 border focus:outline-none"
                  />
                  <select
                    value={fData.timeUnit}
                    onChange={(e) =>
                      setFdata({ ...fData, timeUnit: e.target.value })
                    }
                    className="w-1/2 px-4 py-2 border focus:outline-none"
                  >
                    <option value="">Unit</option>
                    <option value="minutes">Minutes</option>
                    <option value="hours">Hours</option>
                    <option value="days">Days</option>
                    <option value="weeks">Weeks</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="flex flex-col space-y-2">
              <label htmlFor="description">Organ Description *</label>
              <textarea
                value={fData.pDescription}
                onChange={(e) =>
                  setFdata({
                    ...fData,
                    error: false,
                    success: false,
                    pDescription: e.target.value,
                  })
                }
                className="px-4 py-2 border focus:outline-none"
                name="description"
                id="description"
                cols={5}
                rows={2}
              />
            </div>
            <div className="flex flex-col mt-4">
              <label htmlFor="image">Organ Images *</label>
              <span className="text-gray-600 text-xs">Must need 2 images</span>
              <input
                onChange={(e) =>
                  setFdata({
                    ...fData,
                    error: false,
                    success: false,
                    pImage: [...e.target.files],
                  })
                }
                type="file"
                accept=".jpg, .jpeg, .png"
                className="px-4 py-2 border focus:outline-none"
                id="image"
                multiple
              />
            </div>
            <div className="flex space-x-1 py-4">
              <div className="w-1/2 flex flex-col space-y-1">
                <label htmlFor="status">Organ Status *</label>
                <select
                  value={fData.pStatus}
                  onChange={(e) =>
                    setFdata({
                      ...fData,
                      error: false,
                      success: false,
                      pStatus: e.target.value,
                    })
                  }
                  className="px-4 py-2 border focus:outline-none"
                >
                  <option value="Active">Active</option>
                  <option value="Disabled">Disabled</option>
                </select>
              </div>
              <div className="w-1/2 flex flex-col space-y-1">
                <label htmlFor="category">Organ Category *</label>
                <select
                  value={fData.pCategory}
                  onChange={(e) =>
                    setFdata({
                      ...fData,
                      error: false,
                      success: false,
                      pCategory: e.target.value,
                    })
                  }
                  className="px-4 py-2 border focus:outline-none"
                >
                  <option disabled value="">
                    Select a category
                  </option>
                  {categories.length > 0 &&
                    categories.map((elem) => (
                      <option value={elem._id} key={elem._id}>
                        {elem.cName}
                      </option>
                    ))}
                </select>
              </div>
            </div>
            <div className="flex space-x-1 py-4">
              <div className="w-1/2 flex flex-col space-y-1">
                <label htmlFor="quantity">Units Available *</label>
                <input
                  value={fData.pQuantity}
                  onChange={(e) => handleNumericInput(e, "pQuantity")}
                  type="number"
                  className="px-4 py-2 border focus:outline-none"
                  id="quantity"
                />
              </div>
            </div>
            <div className="flex flex-col space-y-1 w-full pb-4 md:pb-6 mt-4">
              <button
                style={{ background: "#303031" }}
                type="submit"
                className={`rounded-full text-gray-100 text-lg font-medium py-2 ${
                  submitting ? "bg-gray-500 cursor-not-allowed" : "bg-gray-800"
                }`}
                disabled={submitting}
              >
                {submitting ? "Submitting..." : "Add Organ"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

const AddProductModal = () => {
  const [allCat, setAllCat] = useState([]);

  useEffect(() => {
    const fetchCategoryData = async () => {
      let responseData = await getAllCategory();
      if (responseData.Categories) {
        setAllCat(responseData.Categories);
      }
    };
    fetchCategoryData();
  }, []);

  return (
    <Fragment>
      <AddProductDetail categories={allCat} />
    </Fragment>
  );
};

export default AddProductModal;
