import React, { Fragment, useContext } from "react";
import { useHistory, useLocation } from "react-router-dom";
import "./style.css";

import { logout } from "./Action";
import { LayoutContext } from "../index";
import { isAdmin } from "../auth/fetchApi";

const Navber = (props) => {
  const history = useHistory();
  const location = useLocation();

  const { data, dispatch } = useContext(LayoutContext);


  const navberToggleOpen = () =>
    data.navberHamburger
      ? dispatch({ type: "hamburgerToggle", payload: false })
      : dispatch({ type: "hamburgerToggle", payload: true });

  const loginModalOpen = () =>
    data.loginSignupModal
      ? dispatch({ type: "loginSignupModalToggle", payload: false })
      : dispatch({ type: "loginSignupModalToggle", payload: true });

  const cartModalOpen = () =>
    data.cartModal
      ? dispatch({ type: "cartModalToggle", payload: false })
      : dispatch({ type: "cartModalToggle", payload: true });
  
  const isAdmin = () => {
  if (typeof window === "undefined") return false;

  const jwt = localStorage.getItem("jwt");
  if (!jwt) return false;

  try {
    const data = JSON.parse(jwt);
    return data.user && data.user.role === 1;
  } catch (err) {
    return false;
  }
};


  return (
    <Fragment>
      {/* Navber Section */}
      <nav className="fixed top-0 w-full z-20 shadow-lg lg:shadow-none bg-slate-900">
  <div className="m-4 md:mx-12 md:my-6 grid grid-cols-4 lg:grid-cols-3">
    <div className="hidden lg:block col-span-1 flex text-slate-300 mt-1">
      <span
        className="hover:bg-slate-700 px-4 py-3 rounded-lg font-light tracking-widest hover:text-white cursor-pointer"
        onClick={(e) => history.push("/")}
      >
        View Organs
      </span>
    </div>
    <div className="col-span-2 lg:hidden flex justify-items-stretch items-center">
      <svg
        onClick={(e) => navberToggleOpen()}
        className="col-span-1 lg:hidden w-8 h-8 cursor-pointer text-slate-300"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
      </svg>
      <span
        onClick={(e) => history.push("/")}
        style={{ letterSpacing: "0.10rem" }}
        className="flex items-left text-center font-bold uppercase text-white text-2xl cursor-pointer px-2 text-center"
      >
        OrganEase
      </span>
    </div>
    <div
      onClick={(e) => history.push("/")}
      style={{ letterSpacing: "0.70rem" }}
      className="hidden lg:block flex items-left col-span-1 text-center text-white font-bold tracking-widest uppercase text-2xl cursor-pointer"
    >
      OrganEase
    </div>
    <div className="flex items-right col-span-2 lg:col-span-1 flex justify-end">
      {localStorage.getItem("jwt") ? (
        <Fragment>
          <div className="userDropdownBtn hover:bg-slate-700 px-2 py-2 rounded-lg relative" title="Logout">
            <svg
              className="cursor-pointer w-8 h-8 text-slate-300 hover:text-white"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"
              />
            </svg>
            <div className="userDropdown absolute right-0 mt-1 bg-slate-800 rounded">
              {/* ...inner dropdown logic remains the same */}
              <li className="flex flex-col text-white w-48 shadow-lg">
                <span
                  onClick={(e) => history.push("/user/orders")}
                  className="flex space-x-2 py-2 px-8 hover:bg-slate-600 cursor-pointer"
                >
                  <span>
                    <svg
                      className="w-8 h-8 text-white hover:text-green-400"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth="2"
                        d="M12 19V5M5 12l7-7 7 7"
                      />
                    </svg>
                  </span>
                  <span>Requests</span>
                </span>
                {/* Other menu items use hover:bg-slate-600, text-white etc */}
              </li>
            </div>
          </div>
        </Fragment>
      ) : (
        <div
          onClick={(e) => loginModalOpen()}
          className="cursor-pointer hover:bg-slate-700 px-2 py-2 rounded-lg"
          title="Login"
        >
          <svg
            className="w-8 h-8 text-slate-300 hover:text-white"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1"
            />
          </svg>
        </div>
      )}
    </div>
  </div>
</nav>
      {/* End Navber Section */}
    </Fragment>
  );
};

export default Navber;
