import React, { Fragment, useState, useContext } from "react";
import { loginReq } from "./fetchApi";
import { LayoutContext } from "../index";

const Login = (props) => {
  const { data: layoutData, dispatch: layoutDispatch } = useContext(
    LayoutContext
  );

  const [data, setData] = useState({
    email: "",
    password: "",
    error: false,
    loading: true,
  });

  const alert = (msg) => <div className="text-xs text-red-500">{msg}</div>;

  const formSubmit = async () => {
    setData({ ...data, loading: true });
    try {
      let responseData = await loginReq({
        email: data.email,
        password: data.password,
      });
      if (responseData.error) {
        setData({
          ...data,
          loading: false,
          error: responseData.error,
          password: "",
        });
      } else if (responseData.token) {
        setData({ email: "", password: "", loading: false, error: false });
        localStorage.setItem("jwt", JSON.stringify(responseData));
        window.location.href = "/";
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Fragment>
      <div className="text-center text-2xl mb-6">Login</div>
      {layoutData.loginSignupError ? (
        <div className="bg-red-200 py-2 px-4 rounded">
          You need to login to Request Organs. Don't have an account ? Create new one.
        </div>
      ) : (
        ""
      )}
      <form className="space-y-6">
  {/* Email Field */}
  <div className="relative">
    <input
      id="email"
      type="email"
      value={data.email}
      onChange={(e) => {
        setData({ ...data, email: e.target.value, error: false });
        layoutDispatch({ type: "loginSignupError", payload: false });
      }}
      className={`peer w-full px-4 pt-6 pb-2 rounded-xl border ${
        data.error ? "border-red-500" : "border-gray-300"
      } bg-white/5 text-white placeholder-transparent focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200`}
      placeholder="Email address"
    />
    <label
      htmlFor="email"
      className="absolute left-4 top-2 text-sm text-gray-300 peer-placeholder-shown:top-4 peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-400 transition-all duration-200"
    >
      Email address
    </label>
  </div>

  {/* Password Field */}
  <div className="relative">
    <input
      id="password"
      type="password"
      value={data.password}
      onChange={(e) => {
        setData({ ...data, password: e.target.value, error: false });
        layoutDispatch({ type: "loginSignupError", payload: false });
      }}
      className={`peer w-full px-4 pt-6 pb-2 rounded-xl border ${
        data.error ? "border-red-500" : "border-gray-300"
      } bg-white/5 text-white placeholder-transparent focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200`}
      placeholder="Password"
    />
    <label
      htmlFor="password"
      className="absolute left-4 top-2 text-sm text-gray-300 peer-placeholder-shown:top-4 peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-400 transition-all duration-200"
    >
      Password
    </label>
  </div>

  {/* Submit Button */}
  <div
    onClick={() => formSubmit()}
    className="bg-blue-600 hover:bg-blue-700 transition-colors duration-200 text-white font-semibold py-3 rounded-xl text-center cursor-pointer shadow-md"
  >
    Login
  </div>
</form>

    </Fragment>
  );
};

export default Login;
